import React from "react";
import EachBook from "./EachBook";

export default function BookList({ booklist, value, addBook }) {
  let currentlyReading = JSON.parse(localStorage.getItem("currentlyReading"));
  let wantToRead = JSON.parse(localStorage.getItem("wantToRead"));
  let read = JSON.parse(localStorage.getItem("read"));

  const getNoneBookCategory = (array, value, book) => {
    if (array.findIndex((e) => e.id === book.id) > -1) {
      return (
        <EachBook key={book.id} book={book} value={value} addBook={addBook} />
      );
    }
  };

  return !booklist ? (
    <div class='spinner-border' role='status'>
      <span class='sr-only'>Loading...</span>
    </div>
  ) : (
    <ol className='books-grid'>
      {booklist.map((book) => {
       return getNoneBookCategory (currentlyReading ,"currentlyReading",book)
         if (wantToRead.findIndex((e) => e.id === book.id) > -1) {
          return (
            <EachBook
              key={book.id}
              book={book}
              value='wantToRead'
              addBook={addBook}
            />
          );
        }
      })}
    </ol>
  );
}
