import { useState } from "react";

import BookShelf from "../Component/BookShelf";
import SearchPage from "../Component/SearchPage";

export default function HomeScreen() {
  const [showSearchPage, setShowSearchpage] = useState(false);
  const [currentRead, setcurrentRead] = useState([]);
  const [wantRead, setwantRead] = useState([]);
  const [read, setRead] = useState([]);
  const changeSearchPage = () => {
    setShowSearchpage(!showSearchPage);
  };

  return (
    <div className='app'>
      {showSearchPage ? (
        <SearchPage changeSearchPage={changeSearchPage} />
      ) : (
        <div className='list-books'>

          <div className='list-books-title'>
            <h1>MyReads</h1>
          </div>

          <div className='list-books-content'>
            <BookShelf title={"Currently Reading"} booklist={currentRead} />
            <BookShelf title={"Want Read"} booklist={wantRead} />
            <BookShelf title={"Read"} booklist={read}/>
          </div>

          <div className='open-search'>
            <a onClick={() => setShowSearchpage(!showSearchPage)}>Add a book</a>
          </div>

        </div>
      )}
    </div>
  );
}
