import React from "react";
import EachBook from "./EachBook";

export default function BookList({ booklist, value, addBook }) {
  let currentlyReading = JSON.parse(localStorage.getItem("currentlyReading"));
  let wantToRead = JSON.parse(localStorage.getItem("wantToRead"));
  let read = JSON.parse(localStorage.getItem("read"));

  return !booklist ? (
    <div class='spinner-border' role='status'>
      <span class='sr-only'>Loading...</span>
    </div>
  ) : (
    <ol className='books-grid'>
      {booklist.map((book) => {
       return currentlyReading.map((e) => {
          if (book.id == e.id) return<EachBook key={book.id} book={book} value="currentlyReading" addBook={addBook} />
        });
      })}
    </ol>
  );
}
