import React from "react";
import PropTypes from "prop-types";

import EachBook from "./EachBook";

export default function BookList({ booklist, addBook }) {
  let allBooks = [
    {
      array: JSON.parse(localStorage.getItem("currentlyReading")),
      value: "currentlyReading",
    },
    {
      array: JSON.parse(localStorage.getItem("wantToRead")),
      value: "wantToRead",
    },
    { array: JSON.parse(localStorage.getItem("read")), value: "read" },
  ];

  return booklist == null ? (
    <div className='d-flex justify-content-center'>
      <div className='spinner-border' role='status'>
        <span className='sr-only'></span>
      </div>
    </div>
  ) : booklist.length == 0 ? (
    <div className='container-fluid d-flex justify-content-center mt-3'>
      <div className='row'>
        <div className='col-md-12'>
          <div className='col-sm-12 empty-cart-cls text-center'>
            {addBook ? (
              <h5>No Book Yet</h5>
            ) : (
              <div>
                <img
                  src='https://cdn-icons-png.flaticon.com/512/1178/1178479.png'
                  width='130'
                  height='130'
                  className='img-fluid mb-4 mr-3'
                />
                <h3>
                  <strong>No Book Found</strong>
                </h3>
                <a
                  href='/'
                  className='btn btn-success cart-btn-transform m-3'
                  data-abc='true'>
                  Back To My Shelf
                </a>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  ) : (
    <ol className='books-grid'>
      {booklist.map((book) => {
        return allBooks.map((e) => {
          if (e.array.findIndex((e) => e.id === book.id) > -1) {
            return (
              <EachBook
                key={book.id}
                book={book}
                bookshelf={e.value}
                addBook={addBook}
              />
            );
          }else{
            return  <EachBook
            key={book.id}
            book={book}
            addBook={addBook}
          />
          }
        });
      })}
    </ol>
  );
}
BookList.propTypes = {
  booklist: PropTypes.array.isRequired,
  addBook: PropTypes.func,
};
