import React from "react";
import DropDown from "./DropDown";

export default function EachBook({ book, value, addBook }) {
  const handleSelectChange = (e) => {
    //get localStorage arr
    const currentlyReading = JSON.parse(
      localStorage.getItem("currentlyReading")
    );
    const wantToRead = JSON.parse(localStorage.getItem("wantToRead"));
    const read = JSON.parse(localStorage.getItem("read"));

    if (e.target.value == "none") return;
    const CurrentlyUpdating = JSON.parse(localStorage.getItem(e.target.value));

    //To prevent Duplication
    let finalArray = CurrentlyUpdating.filter(
      (element) => element.id !== book.id
    );

    //Checking if the book is in another Shelf and delete it 
    addingToAnotherShelf(e, currentlyReading, wantToRead, read);

    //adding the book to the array
    finalArray.push(book);
    localStorage.setItem(e.target.value, JSON.stringify(finalArray));

    //To setSate in HomeScreen
    addBook && addBook(e.target.value, finalArray);
  };

  const addingToAnotherShelf = (e, currentlyReading, wantToRead, read) => {
    switch (e.target.value) {
      case "currentlyReading":
        // if the shelf is in wantToRead
        checkBookSheilf(wantToRead, "wantToRead");
        // if the shelf is in Read
        checkBookSheilf(read, "read");
        break;
      case "wantToRead":
        // if the shelf is in currentlyReading
        checkBookSheilf(currentlyReading, "currentlyReading");
        // if the shelf is in Read
        checkBookSheilf(read, "read");
        break;
      case "read":
        // if the shelf is in currentlyReading
        checkBookSheilf(currentlyReading, "currentlyReading");
        // if the shelf is in WantToRead
        checkBookSheilf(wantToRead, "wantToRead");
        break;
      default:
        break;
    }
    
  };
  const checkBookSheilf = (Bookarray, categoryOfBook) => {
    let switchedIndex = Bookarray.findIndex((e) => e.id === book.id);
    if (switchedIndex > -1) {
      Bookarray.splice(switchedIndex, 1);
      localStorage.setItem(categoryOfBook, JSON.stringify(Bookarray));
      addBook && addBook(categoryOfBook, Bookarray);
    }
  };

  return (
    <li>
      <div className='book'>
        <div className='book-top'>
          <div
            className='book-cover'
            style={{
              width: 128,
              height: 193,
              backgroundImage: `url(${book.imageLinks.thumbnail})`,
            }}></div>
          <DropDown value={value} handleSelectChange={handleSelectChange} />
        </div>
        <div className='book-title'>{book.title}</div>
        {book.authors.map((val, i) => {
          return (
            <div key={i} className='book-authors'>
              {val}
            </div>
          );
        })}
      </div>
    </li>
  );
}
